import React from 'react'
import { Link } from 'react-router-dom'

import { Helmet } from 'react-helmet'

import './login.css'
var finalMobileNumber = "";
const Login = (props) => {
  var drId =localStorage.getItem('drId');
  var drName =localStorage.getItem('drName');
  const [mobile, setMobile] = React.useState({});


  //for work
  const signIn = async () => {
    const payload = {
      requestedRole: "PATIENT",
    };
    const responce = await axios({
      method: "post",
      url: `https://api.qupdev.com/userauth/users/sign-in/` + finalMobileNumber,
      // url: `http://68.183.83.230:8765/userauth/users/sign-in/`+finalMobileNumber,
      data: payload, 

      headers: {
        "Content-Type": "application/json",
      },
    });
    console.log("linkId", responce.data);
    // setMobile(responce.data);

  };

  return (
    <div className="login-container">
      <Helmet>
        <title>Login - Planical modern template</title>
        <meta property="og:title" content="Login - Planical modern template" />
      </Helmet>
      <section className="login-section">
        <div className="login-hero">
          <div className="login-content">
            <main className="login-main">
              <img
                alt="image"
                src="https://max-website20-images.s3.ap-south-1.amazonaws.com/Dr_Puneet_sq_3be6907e0c.jpg"
                className="login-image"
              />
              <header className="login-header">
                <h1 className="login-heading">{drName}</h1>
                <span className="login-caption">
                {drId}
                </span>
                <input
                  type="number"
                  name="Mobile number"
                  required
                  autoFocus
                  onChange={finalMobileNumber}
                  placeholder="Mobile number"
                  autoComplete="on"
                  className="login-textinput input"
                />
              </header>
              <div className="login-buttons">
                <Link to="/verify-o-t-p" onClick={signIn} className="login-navlink">
                  <div className="login-get-started button">
                    <span className="login-text">
                      <span>Send OTP</span>
                      <br></br>
                    </span>
                  </div>
                </Link>
              </div>
              <span className="login-caption1">Powered by Q UP</span>
            </main>
          </div>
        </div>
      </section>
    </div>
  )
}

export default Login
